var attendees = [
  'Jacob',
  'Matthew',
  'Harold',
  'James',
  'Josh'
];


//For loop assignment
for (var i = 0; i < attendees.length; i++) {
 console.log(attendees[i]);
}

// while loop assignment
var i = 0;
while (i < attendees.length) {
 console.log(attendees[i]);
 i++;
}

//do while loop assignment
var i = 0;
do {
  console.log(attendees[i]);
  i++;
} while (i < attendees.length)